<!-- 证件扫面页面 -->
<template>
	<div class="manage-main-contents">
		<Headerbtns :title="page_title">
			<i class="fa fa-angle-left" @click="cancel" slot="left"></i>
		</Headerbtns>
		<div class="content">
			<div style="background: #C6C6C6;width: 251px;height: 158px;margin:50px auto;">
				<img id="renxiangmian" src="../../../static/img/common/sfz_front.png" style="width: 251px;height: 158px;" @click="scanning(0)" />
			</div>
			<div style="background: #C6C6C6;width: 251px;height: 158px;margin:50px auto;">
				<img id="guohuimian" src="../../../static/img/common/sfz_back.png" style="width: 251px;height: 158px;" @click="scanning(1)"/>
			</div>
			<Footerbtns :is_show=false :disabled="true"
				total_premium="00.00">
				<button class="btnFoote" slot="center" @click="ocr">
					证件识别
				</button>
			</Footerbtns>
		</div>
		<alert ref="alert"></alert>
	</div>
</template>

<!--js-->
<script>
import { Indicator } from 'mint-ui'
import { MessageBox } from 'mint-ui'
import global_constant from '../../../src/assets/data/global_constant.json'
import Headerbtns from '../../../src/components/policy/common/Header.vue'
import Footerbtns from '../../../src/components/policy/common/Footer.vue'
import alert from '../../../src/components/policy/common/alert.vue'

export default{
	name:'id_card_scanning',
	components: {Headerbtns,Footerbtns,alert},
	props: ['value'],
	data () {
	    return {
		renxiangmianimg:"",
		guohuimianimg:"",
	    page_title:'证件扫描',
	      	search_tag:'',
		    search_parameter:{
		      customerName:'',
		      phoneNumber:''
		    },
		    customerInfo:{},
		    global_constant:global_constant
	    }
  	},
  	beforeMount(){

  	},
  	mounted(){
  		this.search_tag = this.$route.params.tags
	},
	watch:{
	'value.isopen': {
        handler(val,oldval) {
          if (val!=oldval) {
             this.renxiangmianimg = "",
			 this.guohuimianimg = "",
			 this.init()
          }
        },
        deep: true
      },
	},
  	methods:{
		init:function(){
			var renxiangmian = document.getElementById('renxiangmian');
			renxiangmian.style.display = 'block';
			renxiangmian.src = "data:image/png;base64,";
			var guohuimian = document.getElementById('guohuimian');
			guohuimian.style.display = 'block';
			guohuimian.src = "data:image/png;base64,";
		},
		showMsg:function(msg){
			if(msg!=undefined && msg!=""){
				this.$refs.alert.showMOdal(msg)
			}
		},
      	scanning:function (tag) {
       		let platform = globalConfig.os
       		if(tag == "0"){
				MAPlugin.hybridCallAction('hybrid://MAOcrIdCard:401/invokeOcrIdCard?0', (callbackSucc)=>{
					var renxiangmian = document.getElementById('renxiangmian');
					renxiangmian.style.display = 'block';
					renxiangmian.src = "data:image/jpeg;base64," + callbackSucc.idcardImg;
					this.renxiangmianimg = callbackSucc.idcardImg
				}, function(callbackFail){
					this.showMsg(callbackFail)
				});
       		}else if(tag == "1"){
				MAPlugin.hybridCallAction('hybrid://MAOcrIdCard:401/invokeOcrIdCard?1', (callbackSucc)=>{
					var guohuimian = document.getElementById('guohuimian');
					guohuimian.style.display = 'block';
					guohuimian.src = "data:image/jpeg;base64," + callbackSucc.idcardImg;
					this.guohuimianimg = callbackSucc.idcardImg
				}, function(callbackFail){
					this.showMsg(callbackFail)
				});
       		}
      	},
      	cancel:function () {
			this.value.isopen = !this.value.isopen
			this.value.cusItem = undefined
			console.log("取消识别客户===>",this.value)
		},
      	ocr:function () {
			if(this.renxiangmianimg != "" && this.guohuimianimg != ""){
				let customerType = ""
				if(this.value.type == global_constant.constant.TAG_SEARCH_CUS_APPLICANT){
					customerType = "1"
				}else if(this.value.type == global_constant.constant.TAG_SEARCH_CUS_INSURE){
					customerType = "2"
				}else if(this.value.type == global_constant.constant.TAG_SEARCH_CUS_BNF){
					customerType = "3-"+(this.value.index+1)
				}
				let requ = {
                	"policy":{
						"orderCode": utils.cache.get('orderCode'),
						"agentCode": utils.cache.get('personsal').agentCode,
						"applyCode": "",
						"customerType":customerType,
						"policyCode": "",
						"functionCode": "",
						"idCardFront": this.renxiangmianimg,
						"idCardBack": this.guohuimianimg
					}
              	}
				console.log("识别身份证件")
				console.log(this.policy)
				utils.http.postFast('OCRIDCARD',requ,(body)=> {
					let policy = body.policy
					if(policy.resultCode == 0){
						let item = {}
						item.name = policy.name //姓名
						item.id_card_number = policy.id_card_number //证件号
						let birthdayarr = policy.birthday.split('-')
						let birthday = ""
						if(birthdayarr[1] < 10 && birthdayarr[1].length == 1){
							birthdayarr[1] = "0"+birthdayarr[1]
						}
						if(birthdayarr[2] < 10 && birthdayarr[2].length == 1){
							birthdayarr[2] = "0"+birthdayarr[2]
						}
						item.birthday = birthdayarr[0]+"-"+birthdayarr[1]+"-"+birthdayarr[2] //生日
						item.valid_date = policy.valid_date.
							substring(11,policy.valid_date.length).
							split('.').join('-') //有效期
						if(policy.gender != undefined && policy.gender != ""){
							if(policy.gender == "男"){
								item.gender = "1"
							}else if(policy.gender == "女"){
								item.gender = "2"
							}
						}
						this.value.isopen = !this.value.isopen
						this.value.cusItem = item
						console.log("身份证识别信息返回===>"+JSON.stringify(body))
					}else{
						this.showMsg(body.resultMessage)
					}
				})
			}else{
				this.showMsg("请完成拍摄证件后进行证件识别！")
			}
	  	}
	}
}
</script>

